sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Add your code here
	var sysparm_table = current.getTableName();
	var sysparm_sys_id = current.sys_id;
	var request = new sn_ws.RESTMessageV2('SLX_Attach_VistaPDF', 'POST_URL');
	gs.info('*******REQUEST  '+request);
	var path = "https://" + gs.getProperty("instance_name") + ".service-now.com/";
	var sysparam_view = "PDF_a_Cliente"; // vista pdf a cliente
	var url = path + sysparm_table + '.do?PDF&sys_id=' + sysparm_sys_id + '&sysparm_collection_relationship=false&sysparm_collection=false&sysparm_view='+sysparam_view+'&related_list_filter=&sysparm_domain=';

	request.setEndpoint(url);
	request.saveResponseBodyAsAttachment(sysparm_table,sysparm_sys_id,current.number+'.pdf');
	var response = request.execute();
	if(response.getStatusCode() == '200'){
		/*var userList = [];*/
