sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
    var cont = 0;
	//Se obtiene la cantidad de epp rechazados
    var gr = new GlideRecord("x_hocsa_epp_aprobacion_epp");
    gr.addQuery("u_slx_solicitud_epp", current.document_id);
    gr.addQuery("u_slx_estatus_solicitud", 'rechazado');
    gr.query();
    var q = gr.getRowCount();
	//Se obtiene la cantidad de todos los epp solicitados
    var gr1 = new GlideRecord("x_hocsa_epp_aprobacion_epp");
    gr1.addQuery("u_slx_solicitud_epp", current.document_id);
    gr1.query();
    var q1 = gr1.getRowCount();
	//Se hace un GlideRecord a la solicitud
    var epp = new GlideRecord('x_hocsa_epp_solicitudes_epp');
    epp.addQuery('sys_id', current.document_id);
    epp.query();


    if (epp.next()) {
        if (q != q1) {
            if (q > 0) {
				//En caso que la cantidad de epp rechazado sea mayor a 0 es parcial
                epp.u_slx_tipo_aprobacion = 'parcial';
            } else {
                epp.u_slx_tipo_aprobacion = 'total'; //En caso contrario es total
            }
            epp.update();

        } else {
			//En caso de que se hayan rechazados los EPP pero la aprobación se aprueba
            gs.addErrorMessage('No puede aprobar si todos los EPP estan rechazados.');
            current.state = 'requested';

        }

		//Se actualiza el estatus del epp a aprobado
        var eppPendiente = new GlideRecord("x_hocsa_epp_aprobacion_epp");
        eppPendiente.addQuery("u_slx_solicitud_epp", current.document_id);
        eppPendiente.addQuery("u_slx_estatus_solicitud", 'pendiente');
        eppPendiente.query();
        while (eppPendiente.next()) {
            eppPendiente.u_slx_estatus_solicitud = 'aprobado';
			eppPendiente.update();
        }
    }





})(current, previous);
