sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    autoCloseEpp(); //se llama a la funcion de cierre

    function autoCloseEpp() {
        var ps = gs.getProperty('x_hocsa_epp.autoclose.time'); //Se obtiene la cantidad de días de cierre
        var pn = parseInt(ps);
        var queryTime = new GlideDateTime();
        queryTime.addDaysUTC(-pn);

        if (pn > 0) {
			//GlideRecord a la solicitud EPP que esta Entregado
            var gr = new GlideRecord('x_hocsa_epp_solicitudes_epp');
            gr.addQuery('state', 15);
            gr.addQuery('sys_updated_on', '<', queryTime);
            gr.query();
            while (gr.next()) {
                gr.state = 3;//En caso de pasada la cantidad de días se deja en recibido conforme
                gr.work_notes = 'Solicitud fue Cerrada como Recibida conforme luego de ' + pn + ' días de estar en estado EPP Entregado.';
                gr.update();
            }
        }
    }

})(current, previous);
