sys_script_include.script:
var slx_asesores_ssa = Class.create();
slx_asesores_ssa.prototype = {
    initialize: function() {},

    asesores: function() {
        var id = "";
        var gr = new GlideRecord("sys_user_grmember");
        gr.addQuery("group", gs.getProperty('x_hocsa_epp.slx.grupo.asesores.ssa'));
        gr.query();
        while (gr.next()) {

            if (id.length > 0) {
                id += ',' + gr.user.sys_id.toString();
            } else {
                id = gr.user.sys_id.toString() + '';
            }

        }

        return 'sys_idIN' + id;



    },

	supervisores: function() {
        var id = "";
        var gr = new GlideRecord("sys_user_grmember");
        gr.addQuery("group", gs.getProperty('x_hocsa_epp.slx.grupo.supervisores.epp'));
        gr.query();
        while (gr.next()) {

            if (id.length > 0) {
                id += ',' + gr.user.sys_id.toString();
            } else {
                id = gr.user.sys_id.toString() + '';
            }

        }

        return 'sys_idIN' + id;



    },

    type: 'slx_asesores_ssa'
};
